declare class Packet {
    constructor();
    constructor(opcode : number, size : number, raw? : boolean);
    null: boolean;
    valid: boolean;
    GetSize() : number;
    GetOpcode() : number;
    SetOpcode(op : number) : void;
    GetRawOpcode() : number;
    SetRawOpcode(op : number) : void;
    WriteInt8(offset : number, value : number) : void;
    WriteInt16(offset : number, value : number) : void;
    WriteInt32(offset : number, value : number) : void;
    WriteInt64(offset : number, value : number) : void;
    WriteFloat(offset : number, value : number) : void;
    WriteDouble(offset : number, value : number) : void;
    WriteString(offset : number, value : string) : void;
    WriteFixedLengthString(offset : number, value : string, string_length : number) : void;
    ReadInt8(offset : number) : number;
    ReadInt16(offset : number) : number;
    ReadInt32(offset : number) : number;
    ReadInt64(offset : number) : number;
    ReadFloat(offset : number) : number;
    ReadDouble(offset : number) : number;
    ReadString(offset : number) : string;
    ReadFixedLengthString(offset : number, string_length : number) : string;
}

